        private string BaseListTemplate = "<li style=\"font-size: 16px; text-align: left; line-height: 1.5; mso-line-height-alt: 24px;\"><span style=\"font-size: 16px;\"><a href=\"NOTE-LINK\" rel=\"noopener\" style=\"text-decoration: underline; color: #f5ffdd;\" target=\"_blank\">NOTE-NAME</a></span></li>";
        #endregion
        
        private readonly IConfiguration _configuration;

        public EmailGenerator(IConfiguration configuration)
        {
            _configuration = configuration;
        }

        public Email GenerateSubEmail(Subscriptions sub) 
        {
            var emailContent = CompileEmailFromTemplate(BaseSubscriptionTemplate, sub);
            return new Email(sub.Email, emailContent);
        }

        public List<Email> GenerateBroadcast(List<Notes> notes, List<Subscriptions> subs)
        {
            var emailList = new List<Email>();
            foreach (var sub in subs)
            {
                var emailContent = CompileEmailFromTemplate(BaseBroadcastTemplate, sub, notes);
                emailList.Add(new Email(sub.Email, emailContent));
            }
            return emailList;
        }

        private string CompileEmailFromTemplate(string template, Subscriptions sub, List<Notes> notes = null)
        {
            template = template.Replace("INJECT-NAME", sub.Name);
            template = template.Replace("INJECT-YEAR", DateTime.Now.Year.ToString());
            template = template.Replace("INJECT-UNSUBSCRIBE", GetUnsubLink(sub));
            template = template.Replace("INJECT-NOTES", GetNotesList(notes));

            return template;
        }

        private string GetUnsubLink(Subscriptions sub)
        {
            var baseUrl = _configuration.GetValue<string>("BaseUnsubscribeEmail");
            return baseUrl + "/Subscription/Unsubscribe/" + sub.ID.ToString();
        }

        private string GetNotesList(List<Notes> notes)
        {
            var result = "";
            if (notes == null)
                return result;

            var baseUrl = _configuration.GetValue<string>("BaseUnsubscribeEmail");
            foreach(var note in notes)
            {
                var noteUrl = baseUrl + "/Notes/Details/" + note.ID;
                result += BaseListTemplate.Replace("NOTE-LINK", noteUrl).Replace("NOTE-NAME", note.Title);
            }

            return result;
        }
    }
